import React, { useRef } from "react";

import { Props } from "./types";

import {
  HCARD_BUILDER_TITLE,
  PERSONAL_DETAILS_LEGEND,
  ADDRESS_LEGEND,
  INPUT_FIELDS_PERSONAL_DETAILS,
  INPUT_FIELDS_ADDRESS,
} from "./constants";

import {
  HCardFormSection,
  HCardFormTitle,
  FieldSet,
  Legend,
  FieldSetContainer,
  Label,
  LabelText,
  Input,
  ButtonsContainer,
  Button,
  UploadButton,
} from "./styled";

export const HCardForm = ({
  hCardState,
  setAvatarState,
  handleChange,
}: Props) => {
  const {
    givenName,
    surname,
    email,
    phone,
    houseNameOrNumber,
    street,
    suburb,
    state,
    postcode,
    country,
  } = hCardState;

  const fileInput = useRef(null);

  const handleOnChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value, name } = e.target;
    handleChange(value, name);
  };

  const handleOnUploadClick = (e: React.MouseEvent<HTMLInputElement>) => {
    fileInput.current.click();
  };
  const handleOnUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setAvatarState({
        file: file,
        previewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  };

  return (
    <HCardFormSection>
      <HCardFormTitle>{HCARD_BUILDER_TITLE}</HCardFormTitle>
      <form>
        <FieldSet>
          <Legend>{PERSONAL_DETAILS_LEGEND}</Legend>
          <FieldSetContainer>
            {INPUT_FIELDS_PERSONAL_DETAILS.map((input) => {
              const { label, id, type } = input;
              return (
                <Label htmlFor={id}>
                  <LabelText>{label}</LabelText>
                  <Input
                    id={id}
                    name={id}
                    type={type}
                    value={id}
                    onChange={handleOnChange}
                  />
                </Label>
              );
            })}
          </FieldSetContainer>
        </FieldSet>
        <FieldSet>
          <Legend>Address</Legend>
          <FieldSetContainer>
            {INPUT_FIELDS_ADDRESS.map((input) => {
              const { label, id, type } = input;
              return (
                <Label htmlFor={id}>
                  <LabelText>{label}</LabelText>
                  <Input
                    id={id}
                    name={id}
                    type={type}
                    value={id}
                    onChange={handleOnChange}
                  />
                </Label>
              );
            })}
          </FieldSetContainer>
        </FieldSet>
        <ButtonsContainer>
          <Button type="button" onClick={handleOnUploadClick}>
            Upload Avatar
          </Button>
          <UploadButton type="file" ref={fileInput} onChange={handleOnUpload} />
          <Button
            type="button"
            buttonStyle="primary"
            onClick={(e) => e.preventDefault()}
          >
            Create hCard
          </Button>
        </ButtonsContainer>
      </form>
    </HCardFormSection>
  );
};
