import React, { useRef } from "react";

import { Props } from "./types";

import {
  HCARD_BUILDER_TITLE,
  PERSONAL_DETAILS_LEGEND,
  ADDRESS_LEGEND,
  INPUT_FIELDS_PERSONAL_DETAILS,
  INPUT_FIELDS_ADDRESS,
} from "./constants";

import {
  HCardFormSection,
  HCardFormTitle,
  FieldSet,
  Legend,
  FieldSetContainer,
  Label,
  LabelText,
  Input,
  ButtonsContainer,
  Button,
  UploadButton,
} from "./styled";

export const HCardForm = ({
  hCardState,
  setAvatarState,
  handleChange,
}: Props) => {
  const {
    givenName,
    surname,
    email,
    phone,
    houseNameOrNumber,
    street,
    suburb,
    state,
    postcode,
    country,
  } = hCardState;

  const fileInput = useRef(null);

  const handleOnChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value, name } = e.target;
    handleChange(value, name);
  };

  const handleOnUploadClick = (e: React.MouseEvent<HTMLInputElement>) => {
    fileInput.current.click();
  };
  const handleOnUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    let reader = new FileReader();
    let file = e.target.files[0];

    reader.onloadend = () => {
      setAvatarState({
        file: file,
        previewUrl: reader.result,
      });
    };

    reader.readAsDataURL(file);
  };

  return (
    <HCardFormSection>
      <HCardFormTitle>{HCARD_BUILDER_TITLE}</HCardFormTitle>
      <form>
        <FieldSet>
          <Legend>{PERSONAL_DETAILS_LEGEND}</Legend>
          <FieldSetContainer>
            <Label htmlFor="givenName">
              <LabelText>Given Name</LabelText>
              <Input
                id="givenName"
                name="givenName"
                type="text"
                value={givenName}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="surname">
              <LabelText>Surname</LabelText>
              <Input
                id="surname"
                name="surname"
                type="text"
                value={surname}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="email">
              <LabelText>Email</LabelText>
              <Input
                id="email"
                name="email"
                type="email"
                value={email}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="phone">
              <LabelText>Phone</LabelText>
              <Input
                id="phone"
                name="phone"
                type="phone"
                value={phone}
                onChange={handleOnChange}
              />
            </Label>
          </FieldSetContainer>
        </FieldSet>
        <FieldSet>
          <Legend>Address</Legend>
          <FieldSetContainer>
            <Label htmlFor="houseNameOrNumber">
              <LabelText>House Name or #</LabelText>
              <Input
                id="houseNameOrNumber"
                name="houseNameOrNumber"
                type="text"
                value={houseNameOrNumber}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="street">
              <LabelText>Street</LabelText>
              <Input
                id="street"
                name="street"
                type="text"
                value={street}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="suburb">
              <LabelText>Suburb</LabelText>
              <Input
                id="suburb"
                name="suburb"
                type="text"
                value={suburb}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="state">
              <LabelText>State</LabelText>
              <Input
                id="state"
                name="state"
                type="text"
                value={state}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="postcode">
              <LabelText>Postcode</LabelText>
              <Input
                id="postcode"
                name="postcode"
                type="text"
                value={postcode}
                onChange={handleOnChange}
              />
            </Label>
            <Label htmlFor="country">
              <LabelText>Country</LabelText>
              <Input
                id="country"
                name="country"
                type="text"
                value={country}
                onChange={handleOnChange}
              />
            </Label>
          </FieldSetContainer>
        </FieldSet>
        <ButtonsContainer>
          <Button type="button" onClick={handleOnUploadClick}>
            Upload Avatar
          </Button>
          <UploadButton type="file" ref={fileInput} onChange={handleOnUpload} />
          <Button
            type="button"
            buttonStyle="primary"
            onClick={(e) => e.preventDefault()}
          >
            Create hCard
          </Button>
        </ButtonsContainer>
      </form>
    </HCardFormSection>
  );
};
