import styled from "styled-components";
import { media } from "common";

export const HCardBuilderLayout = styled.main`
  display: flex;
  flex-direction: column;
  width: 100%;
  max-width: 1000px;
  height: auto;
  margin: auto;
  @media ${media.tablet} {
    flex-direction: row;
  }
`;
